'use strict';

var rpc = require('./rpc.json.js');
var typesCommon = require('./types-common.json.js');
var typesMessage = require('./types-message.json.js');
var typesMetadata = require('./types-metadata.json.js');
var typesProgram = require('./types-program.json.js');
var types = require('../utils/types.js');

const gearRpc = types.transformTypes(rpc).rpc;
const gearTypes = {
    ...types.transformTypes(typesMetadata).types,
    ...types.transformTypes(typesProgram).types,
    ...types.transformTypes(typesMessage).types,
    ...types.transformTypes(typesCommon).types,
};

exports.gearRpc = gearRpc;
exports.gearTypes = gearTypes;
