'use strict';

var programMetadata = require('./programMetadata.js');
var stateMetadata = require('./stateMetadata.js');

function isProgramMeta(arg) {
    if (typeof arg !== 'object') {
        return false;
    }
    return arg instanceof programMetadata.ProgramMetadata;
}
function isStateMeta(arg) {
    if (typeof arg !== 'object') {
        return false;
    }
    return arg instanceof stateMetadata.StateMetadata;
}

exports.isProgramMeta = isProgramMeta;
exports.isStateMeta = isStateMeta;
