'use strict';

var api = require('@polkadot/api');
var util = require('@polkadot/util');

function decodeAddress(publicKey) {
    return util.u8aToHex(new api.Keyring().decodeAddress(publicKey));
}
function encodeAddress(publicKeyRaw) {
    return new api.Keyring().encodeAddress(publicKeyRaw);
}

exports.decodeAddress = decodeAddress;
exports.encodeAddress = encodeAddress;
